const VERTEX_SHADER_SOURCE =
`#version 300 es

void main() {
    gl_Position = vec4(0.0, 0.0, 0.0, 1.0);
    gl_PointSize = 10.0;
}
`

const FRAGMENT_SHADER_SOURCE =
`#version 300 es

precision highp float;

out vec4 fragColor;
void main() {
    fragColor = vec4(1.0, 0.0, 0.0, 1.0);
}
`;

export class GraphicsEngine {
    constructor(private gl: WebGL2RenderingContext) {}

    print() {
        console.log("graphics engine print", this.gl);
    }

    draw() {
        const gl = this.gl

        let vertexShader = compileShader(gl, VERTEX_SHADER_SOURCE, gl.VERTEX_SHADER);
        let fragmentShader = compileShader(gl, FRAGMENT_SHADER_SOURCE, gl.FRAGMENT_SHADER);
        let program = createProgram(gl, vertexShader, fragmentShader);

        //let positionAttributeLocation = gl.getAttribLocation(program, "a_position");

        // Set the color for clearing <canvas> and clear <canvas>
        gl.clearColor(0.0, 0.0, 1.0, 1.0);
        gl.clear(gl.COLOR_BUFFER_BIT);


    }




}

/**
 * Creates and compiles a shader.
 * 
 * @param {!WebGL2RenderingContext} gl The WebGL context.
 * @param {string} shaderSource The GLSL source code for the shader.
 * @param {number} shaderType The type of shader, VERTEX_SHADER or FRAGMENT_SHADER.
 * @return {!WebGLShader} The shader.
 */
function compileShader(gl: WebGL2RenderingContext, shaderSource: string, shaderType: number): WebGLShader {
    // Create the shader object
    let shader = gl.createShader(shaderType);

    // Set the shader source code.
    gl.shaderSource(shader, shaderSource);

    // Compile the shader
    gl.compileShader(shader);

    // Check if it compiled
    const success = gl.getShaderParameter(shader, gl.COMPILE_STATUS);
    if (!success) {
        // Something went wrong during compilation; get the error
        throw "could not compile shader:" + gl.getShaderInfoLog(shader);
    }

    return shader;
}

/** 
 * Creates a program from 2 shaders.
 * 
 * @param {!WebGL2RenderingContext} gl The WebGL context.
 * @param {!WebGLShader} vertexShader A vertex shader.
 * @param {!WebGLShader} fragmentShader A fragment shader.
 * @return {!WebGLProgram} A program.
 */
function createProgram(gl: WebGL2RenderingContext, vertexShader: WebGLShader, fragmentShader: WebGLShader): WebGLProgram {
    // create a program.
    let program = gl.createProgram();

    // attach the shaders.
    gl.attachShader(program, vertexShader);
    gl.attachShader(program, fragmentShader);

    // link the program.
    gl.linkProgram(program);

    // Check if it linked.
    const success = gl.getProgramParameter(program, gl.LINK_STATUS);
    if (!success) {
        // something went wrong with the link
        throw("program failed to link" + gl.getProgramInfoLog(program));
    }

    return program;
}
