import React from "react";

const CANVAS_WIDTH = 1000;
const CANVAS_HEIGHT = 500;

export default class GraphicsPlayground2 extends React.Component {
    constructor(props) {
        super(props);
        this.canvasRef = React.createRef();
        this.gl = null;
    }

    componentDidMount() {
        this.renderer = new THREE.WebGLRenderer({ canvas: this.canvasRef.current, alpha: true});
        this.renderer.setSize(CANVAS_WIDTH, CANVAS_HEIGHT);
    }    

    render() {
        return (
            <div className="graphics_playground_2">
                <h2>graphics playground 2 page desu...</h2>
                <div id="canvas_container">
                    <canvas ref={this.canvasRef} width={CANVAS_WIDTH} height={CANVAS_HEIGHT} />
                </div>
            </div>
        );
    }
}
